mapper.insert:
insert into notice_info(
    ni_title,
    ni_text,
    mi_id
)values(
   #{ni_title},
   #{ni_text},
   #{mi_id}
)

mapper.update:
update notice_info
set ni_title = #{ni_title}, ni_text = #{ni_text}, ni_updatedt = to_char(now(), 'YYYYMMDDHH24MISS'::text)
where ni_num = #{ni_num};

mapper.select:
select A.ni_num, A.ni_title, A.ni_updatedt , A.ni_views, B.mi_name
from notice_info A inner join member_info B on A.mi_id = B.mi_id order by ni_num desc
LIMIT #{PAGE_SIZE} offset #{startIndex}
